        dimCicloMatricula[anoInicioCiclo] <= ano,
        dimCicloMatricula[anoFimCiclo] >= ano,
        dimVagas[nomeVaga]="Vagas Ofertadas",
        SELECTEDVALUE('dCalendário'[Ano])<=2020 || dimFinanciamento[nomeFonteFinanciamentoCorrigido]="Sem Programa Associado",
        dimCurso[NivelCurso] <> "Básico propedêutico",
        dimCurso[NivelCurso] <> "Qualificação Profissional (FIC)"
    )
RETURN
    SUMX(tabela,[Vagas])

